$("._toggle-mob-menu").click(function () {
    $(".header").toggleClass("_open")
    $("body").toggleClass("fixed-body")
})

$(".catalog-btn").mouseover(function () {
    if(window.innerWidth>992){
        $(".catalog-menu").addClass("_open")
    }
})

$(".catalog-btn").click(function () {
    if(window.innerWidth<=992){
        $(this).toggleClass("_open")
        $(".catalog-menu").slideToggle()
    }
})

$(".main-slider").click(function () {
    $(".catalog-menu").removeClass("_open")
})

$(".header-search__toggle").click(function () {
    $(".header-search").toggleClass("_open")
    $("._search-field").focus()
})

$(document).mouseup( function(e){
    var div = $( ".header-search,.catalog-menu" );
    if ( !div.is(e.target)
        && div.has(e.target).length === 0 ) {
        if($(div).hasClass("_open")){
            $(div).removeClass("_open")
        }
    }
});

$("._search-field").on("input",function () {
    if($(this).val().length > 3){
        $(this).parents(".header-search").addClass("_show-results")
    } else{
        $(this).parents(".header-search").removeClass("_show-results")
    }
    $("._search-query").html($(this).val())
})

function mainSlider() {
    var swiper = new Swiper('.main-slider .swiper-container', {
        slidesPerView: 1,
        spaceBetween: 0,
        loop: {
            disableOnInteraction: false,
            pauseOnMouseEnter: true
        },
        autoplay: true,
    })
}

function collectionSlider() {
    var swiper = new Swiper('.collection-slider .swiper-container', {
        slidesPerView: 3,
        spaceBetween: 0,
        autoplay: true,
        navigation: {
            nextEl: '.collection-slider .swiper-button-next',
            prevEl: '.collection-slider .swiper-button-prev',
        },
        breakpoints: {
            320: {
                // autoplay: {
                //     delay: 3000,
                // },
                slidesPerView: 1,
            },
            576: {
                // autoplay: false,
                slidesPerView: 2,
            },
            992: {
                autoplay: false,
                slidesPerView: 3,
            },
        }
    })
}

function Sliders() {
    let SlidersArray = []

    let sliders = document.querySelectorAll(".slider")
    sliders.forEach((slider,index) => {
        slider.setAttribute("data-slider-id",index)

        SlidersArray[index] = new Swiper(`.slider[data-slider-id="${index}"] .swiper-container`, {
            slidesPerView: "auto",
            spaceBetween: 0,
            watchSlidesProgress: true,
            preventClicks :true,
            a11y: false,
            observer: true,
            observeParents: true,
            observeSlideChildren: true,

            navigation: {
                nextEl: `.slider[data-slider-id="${index}"] .swiper-button-next`,
                prevEl: `.slider[data-slider-id="${index}"] .swiper-button-prev`,
            },
            pagination: {
                el: `.slider[data-slider-id="${index}"] .swiper-pagination`,
                type: 'bullets',
                clickable: true,
            },
        })
    })
}

function gallery() {
    let SlidersArray = []

    let sliders = document.querySelectorAll(".gallery")
    sliders.forEach((slider,index) => {
        slider.setAttribute("data-slider-id",index)
        let gallery = {
            mySwiper: {},
            mySwiper2: {}
        }

        gallery.mySwiper = new Swiper(`.gallery[data-slider-id="${index}"] .mySwiper`, {
            spaceBetween: 0,
            slidesPerView: "auto",
            freeMode: true,
            watchSlidesProgress: true,
            direction: "vertical",
            breakpoints: {
                320: {
                    direction: "horizontal",
                },
                992: {
                    direction: "vertical",
                }
            }
        });
        gallery.mySwiper2 = new Swiper(`.gallery[data-slider-id="${index}"] .mySwiper2`, {
            spaceBetween: 0,
            direction: "vertical",
            loop: true,
            navigation: {
                nextEl: `.gallery[data-slider-id="${index}"] .swiper-button-next`,
                prevEl: `.gallery[data-slider-id="${index}"] .swiper-button-prev`,
            },
            thumbs: {
                swiper:  gallery.mySwiper,
            },
            breakpoints: {
                320: {
                    direction: "horizontal",
                },
                992: {
                    direction: "vertical",
                }
            }
        });

        SlidersArray[index] = gallery
    })

}


mainSlider()
Sliders()
gallery()
collectionSlider()

